CREATE OR REPLACE FUNCTION obter_qt_retorno_age_cons ( cd_pessoa_fisica_p text, cd_agenda_p bigint, dt_inicial_p timestamp, dt_final_p timestamp) RETURNS varchar AS $body$
DECLARE

			 
ds_retorno_w	smallint;
qt_total_retorno_w	smallint;		
 

BEGIN 
 
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') and (dt_inicial_p IS NOT NULL AND dt_inicial_p::text <> '') and (dt_final_p IS NOT NULL AND dt_final_p::text <> '') and (cd_agenda_p IS NOT NULL AND cd_agenda_p::text <> '') then 
 
	select	count(a.cd_pessoa_fisica) 
	into STRICT	qt_total_retorno_w 
	from	agenda_consulta a, 
		agenda b 
	where	a.cd_agenda = b.cd_agenda 
	and	b.cd_tipo_agenda = 3 
	and	a.cd_agenda = cd_agenda_p 
	and	a.cd_pessoa_fisica = cd_pessoa_fisica_p 
	and	a.dt_agenda between dt_inicial_p and dt_final_p + 86399/86400 
	and	a.ie_status_agenda not in ('C', 'B');
	 
	ds_retorno_w := qt_total_retorno_w;
		 
end if;	
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
