CREATE OR REPLACE FUNCTION obter_qt_sc_em_ordem_sem_cot ( nr_solic_compra_p bigint, nr_item_solic_compra_p bigint, dt_entrega_p timestamp) RETURNS bigint AS $body$
DECLARE


qt_material_ordem_w	ordem_compra_item_entrega.qt_prevista_entrega%type;


BEGIN

select	coalesce(sum(b.qt_prevista_entrega),0)
into STRICT	qt_material_ordem_w
from	ordem_compra_item a,
	ordem_compra_item_entrega b
where	a.nr_ordem_compra = b.nr_ordem_compra
and	a.nr_item_oci = b.nr_item_oci
and	coalesce(a.nr_cot_compra::text, '') = ''
and	a.nr_solic_compra = nr_solic_compra_p
and	a.nr_item_solic_compra = nr_item_solic_compra_p
and	trunc(b.dt_entrega_original,'dd') = trunc(dt_entrega_p,'dd') /*Tem que usar essa data entrega original, porque nesse campo fica sempre gravado a data de entrega da solicitação. Não pode usar a data prevista entrega porque pode ser alterada e dai nao vai funcionar a comparação*/
and	coalesce(b.dt_cancelamento::text, '') = '';

return	qt_material_ordem_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
