CREATE OR REPLACE FUNCTION obter_qt_senhas_atendidas (nr_seq_local_senha_p bigint) RETURNS bigint AS $body$
DECLARE


qt_senhas_atendidas_w	bigint;


BEGIN

if (coalesce(nr_seq_local_senha_p,0) > 0) then

	select	count(*)
	into STRICT	qt_senhas_atendidas_w
	from	atendimentos_senha
	where	nr_seq_local_chamada = nr_seq_local_senha_p
	and	(dt_inicio_atendimento IS NOT NULL AND dt_inicio_atendimento::text <> '')
	and	(dt_fim_atendimento IS NOT NULL AND dt_fim_atendimento::text <> '')
	and	trunc(dt_inicio_atendimento) = trunc(clock_timestamp());

end if;

return	qt_senhas_atendidas_w;

end;
