CREATE OR REPLACE FUNCTION obter_qt_senha_atend (nr_seq_senha_p bigint) RETURNS bigint AS $body$
DECLARE


qt_senhas_atendidas_w	bigint;


BEGIN

if (coalesce(nr_seq_senha_p,0) > 0) then

	select count(distinct nr_seq_pac_senha_fila)
	into STRICT	qt_senhas_atendidas_w
	from	atendimentos_senha a
	where	(a.dt_inicio_atendimento IS NOT NULL AND a.dt_inicio_atendimento::text <> '')
	and	(a.dt_fim_atendimento IS NOT NULL AND a.dt_fim_atendimento::text <> '')
	and	trunc(a.dt_inicio_atendimento) = trunc(clock_timestamp())
	and	exists (SELECT 1 from atendimentos_senha b where b.nr_seq_pac_senha_fila = nr_seq_senha_p and b.nm_usuario_inicio = a.nm_usuario_inicio);

end if;

return	qt_senhas_atendidas_w;

end;
