CREATE OR REPLACE FUNCTION obter_qt_solucao_total_md (ie_tipo_dosagem_p text, qt_tempo_aplicacao_p bigint, qt_dosagem_p bigint) RETURNS bigint AS $body$
DECLARE


  result_w bigint;

BEGIN

  if (ie_tipo_dosagem_p = 'GTM') then
    result_w := dividir_sem_round_md((coalesce(qt_dosagem_p, 0) * 60) * coalesce(qt_tempo_aplicacao_p, 0), 20);
  elsif (ie_tipo_dosagem_p = 'MGM') then
    result_w := dividir_sem_round_md((coalesce(qt_dosagem_p, 0) * 60) * dividir_sem_round_md(qt_tempo_aplicacao_p, 3), 20);
  else
    result_w := (coalesce(qt_tempo_aplicacao_p, 0) * coalesce(qt_dosagem_p, 0));
  end if;

  return result_w;
end;
