CREATE OR REPLACE FUNCTION obter_qt_tempo_aplicacao_md (ie_tipo_dosagem_p text, qt_volume_p bigint, qt_dosagem_p bigint) RETURNS bigint AS $body$
DECLARE


  result_w bigint;

BEGIN

  if (ie_tipo_dosagem_p = 'GTM') then
    result_w := dividir_sem_round_md(qt_volume_p , (coalesce(qt_dosagem_p, 0) * 20) * 60);
  elsif (ie_tipo_dosagem_p = 'MGM') then
    result_w := dividir_sem_round_md(qt_volume_p, dividir_sem_round_md((coalesce(qt_dosagem_p, 0) * 20), 3) * 60);
  else
    result_w := dividir_sem_round_md(qt_volume_p, qt_dosagem_p);
  end if;

  return result_w;
end;
