CREATE OR REPLACE FUNCTION obter_qt_tipo_item_sepse ( nr_seq_atributo_p bigint, ie_resultado_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


qt_tipo_w	bigint := 0;
ds_tipo_w	varchar(255);


BEGIN
select	max(ie_tipo)
into STRICT	ds_tipo_w
from	sepse_atributo
where	nr_sequencia = nr_seq_atributo_p;


if ( coalesce(ie_resultado_p,'N') = 'S') then

	if ( ie_opcao_p = 'S') then

		select	max(CASE WHEN ds_tipo_w='S' THEN 1 WHEN ds_tipo_w='G' THEN 0 WHEN ds_tipo_w='A' THEN 1 END )
		into STRICT	qt_tipo_w
		;

	elsif ( ie_opcao_p = 'G') then

		select	max(CASE WHEN ds_tipo_w='S' THEN 0 WHEN ds_tipo_w='G' THEN 1 WHEN ds_tipo_w='A' THEN 1 END )
		into STRICT	qt_tipo_w
		;

	end if;

end if;

return	qt_tipo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
