CREATE OR REPLACE FUNCTION obter_qt_treinamento (ie_status_p text, dt_inicial_p timestamp, dt_final_p timestamp, cd_gerente_p bigint) RETURNS bigint AS $body$
DECLARE


qt_treinamentos_w	bigint;
		
/* OS 1743059
Objetivo de uso: Retornar a quantidade de treinamentos atrasados, pendentes ou concluidos (parametro), de acordo com o periodo e a gerencia informados.
Function utilizada no relatorio "Training Performance Summary - Third Party", desenvolvido para o controle do KPI de treinamentos concluidos

Parametro ie_status_p:
A - Atrasado
C - Concluido
P - Pendente
*/
BEGIN

select	count(*)
into STRICT	qt_treinamentos_w
from	qms_treinamento
where 	((obter_status_treinamento(nr_sequencia, 'C', dt_liberacao, dt_confirmacao) = ie_status_p) or (coalesce(ie_status_p::text, '') = ''))
and	ie_necessario = 'S'
and 	dt_liberacao between inicio_dia(dt_inicial_p) and fim_dia(dt_final_p)
and	cd_gerente_pessoa = coalesce(cd_gerente_p, cd_gerente_pessoa)
and	coalesce(ie_situacao, 'A') = 'A';

return qt_treinamentos_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
