CREATE OR REPLACE FUNCTION obter_qt_ultima_compra_data ( cd_estabelecimento_p bigint, cd_material_p bigint, dt_referencia_p timestamp) RETURNS bigint AS $body$
DECLARE


vl_retorno_w		double precision;
qt_item_nf_w		double precision;
nr_seq_ultima_compra_w	bigint;


BEGIN

select	max(a.qt_item_nf)
into STRICT	qt_item_nf_w
from	nota_fiscal_item a,
	nota_fiscal b
where	a.nr_sequencia	= b.nr_sequencia
and	a.cd_material	= cd_material_p
and	b.ie_situacao	= 1
and	a.dt_atualizacao	<= dt_referencia_p
and	b.nr_sequencia	= (
		SELECT	max(x.nr_sequencia)
		from	natureza_operacao n,
			nota_fiscal x,
			nota_fiscal_item y
		where	x.nr_sequencia = y.nr_sequencia
		and	n.cd_natureza_operacao	= x.cd_natureza_operacao
		and	n.ie_entrada_saida		= 'E'
		and	x.dt_emissao		<= dt_referencia_p
		and	x.ie_acao_nf		= 1
		and	b.ie_situacao		= 1
		and	y.cd_material		= cd_material_p
		and	y.cd_estabelecimento	= cd_estabelecimento_p);

vl_retorno_w	:= qt_item_nf_w;

return	vl_retorno_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
