SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_qt_ultima_compra ( cd_estabelecimento_p bigint, cd_material_p bigint) RETURNS bigint AS $body$
DECLARE


vl_retorno_w		double precision;
qt_item_nf_w		double precision;
nr_seq_ultima_compra_w	bigint;


BEGIN

select	coalesce(max(nr_sequencia),0)
into STRICT	nr_seq_ultima_compra_w
from	sup_dados_ultima_compra
where	cd_material	= cd_material_p
and	cd_estabelecimento	= cd_estabelecimento_p;

if (nr_seq_ultima_compra_w > 0) then

	select	max(a.qt_item_nf)
	into STRICT	qt_item_nf_w
	from	nota_fiscal_item a,
		nota_fiscal b
	where	a.nr_sequencia	= b.nr_sequencia
	and	a.cd_material	= cd_material_p
	and	b.nr_sequencia = (
		SELECT	x.nr_seq_nota
		from	sup_dados_ultima_compra x
		where	x.nr_sequencia = nr_seq_ultima_compra_w);
else

	select	max(a.qt_item_nf)
	into STRICT	qt_item_nf_w
	from	nota_fiscal_item a,
		nota_fiscal b
	where	a.nr_sequencia	= b.nr_sequencia
	and	a.cd_material	= cd_material_p
	and	b.nr_sequencia	= (
			SELECT	max(x.nr_sequencia)
			from	natureza_operacao n,
				operacao_nota o,
				nota_fiscal x,
				nota_fiscal_item y
			where	x.nr_sequencia = y.nr_sequencia
			and	n.cd_natureza_operacao = x.cd_natureza_operacao
			and	n.ie_entrada_saida = 'E'
			and	x.dt_emissao 	>= clock_timestamp() - interval '90 days'
			and	x.ie_acao_nf	= 1
			and	y.cd_material	= cd_material_p
			and	x.cd_operacao_nf = o.cd_operacao_nf
			and	coalesce(o.ie_ultima_compra, 'S') = 'S'
			and	y.cd_estabelecimento = cd_estabelecimento_p);
end if;

vl_retorno_w	:= qt_item_nf_w;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
