CREATE OR REPLACE FUNCTION obter_qt_util_mat_autor ( nr_sequencia_p bigint, nr_atendimento_p bigint, cd_convenio_p bigint, cd_material_p bigint) RETURNS bigint AS $body$
DECLARE


qt_autorizada_w			double precision	:= 0;
qt_utilizada_w			double precision	:= 0;
qt_saldo_w			double precision	:= 0;


BEGIN

/* obtem materiais autorizados */

select 	coalesce(sum(coalesce(a.qt_autorizada,0)),0)
into STRICT	qt_autorizada_w
from	material_autorizado a
where	a.nr_sequencia		= nr_sequencia_p;

/* obter materiais lan√ßados */

begin
select	coalesce(sum(qt_material),0)
into STRICT	qt_utilizada_w
from	material_atend_paciente
where	nr_atendimento		= nr_atendimento_p
and	cd_convenio		= cd_convenio_p
and	cd_material		= cd_material_p
and	coalesce(cd_motivo_exc_conta::text, '') = '';
exception when others then
	qt_utilizada_w := 0;
end;

qt_saldo_w :=	coalesce(qt_autorizada_w,0) - coalesce(qt_utilizada_w,0);

return qt_saldo_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
