CREATE OR REPLACE FUNCTION obter_qt_visitante_unidade ( nr_atendimento_p bigint) RETURNS bigint AS $body$
DECLARE

 
qt_retorno_w		bigint := 1;
cd_setor_w		bigint;	
cd_unid_basica_w	varchar(25);
cd_unid_compl_w		varchar(25);


BEGIN 
if (coalesce(nr_atendimento_p,0) > 0) then 
 
	select 	max(obter_setor_atendimento(nr_atendimento_p)), 
		max(obter_unidade_atendimento(nr_atendimento_p, 'A', 'UB')), 
		max(obter_unidade_atendimento(nr_atendimento_p, 'A', 'UC')) 
	into STRICT	cd_setor_w, 
		cd_unid_basica_w, 
		cd_unid_compl_w 
	;
	 
	select	coalesce(max(qt_max_visitante),1) 
	into STRICT	qt_retorno_w 
	from 	unidade_atendimento 
	where 	cd_setor_atendimento = cd_setor_w 
	and 	cd_unidade_basica  = cd_unid_basica_w 
	and 	cd_unidade_compl   = cd_unid_compl_w;
 
end if;
 
return	qt_retorno_w;
 
end;
$body$
