CREATE OR REPLACE FUNCTION obter_qt_volta_os_vv (nr_seq_ordem_serv_p bigint, ie_tipo_hist_p text) RETURNS bigint AS $body$
DECLARE


qt_w	bigint;


BEGIN

if (ie_tipo_hist_p = 'OK') then
	select	count(*)
	into STRICT	qt_w
	from	man_ordem_serv_tecnico x
	where	nr_seq_ordem_serv	= nr_seq_ordem_serv_p
	and		nr_seq_tipo		= 117
	and		coalesce(dt_inativacao::text, '') = '';
else
	select	count(*)
	into STRICT	qt_w
	from	man_ordem_serv_tecnico x
	where	nr_seq_ordem_serv	= nr_seq_ordem_serv_p
	and		nr_seq_tipo		= 118
	and		coalesce(dt_inativacao::text, '') = '';
end if;

return	qt_w;

end;
