SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_quantidade_aih ( nr_seq_protocolo_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
vl_retorno_w		integer	:= 0;
dt_emissao_w		timestamp;
dt_mesano_referencia_w	timestamp;

/*	I - Quantidade de AIH com data de emissão igual a data de referencia 
	M - Quantidade de AIH com data de emissão menor a data de referencia 
*/
 
 

BEGIN 
 
select	trunc(dt_mesano_referencia) 
into STRICT	dt_mesano_referencia_w 
from	protocolo_convenio 
where	nr_seq_protocolo = nr_seq_protocolo_p;
 
if (ie_opcao_p = 'I') then 
	select	count(*) 
	into STRICT	vl_retorno_w 
	from 	sus_aih			a, 
		conta_paciente		b 
	where	b.nr_interno_conta	= a.nr_interno_conta 
	and	b.nr_seq_protocolo	= nr_seq_protocolo_p 
	and	trunc(a.dt_emissao)	= dt_mesano_referencia_w;
elsif (ie_opcao_p = 'M') then 
	select	count(*) 
	into STRICT	vl_retorno_w 
	from 	sus_aih			a, 
		conta_paciente		b 
	where	b.nr_interno_conta	= a.nr_interno_conta 
	and	b.nr_seq_protocolo	= nr_seq_protocolo_p 
	and	trunc(a.dt_emissao)	< dt_mesano_referencia_w;
end if;
 
return	vl_retorno_w;
 
end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_quantidade_aih ( nr_seq_protocolo_p bigint, ie_opcao_p text) FROM PUBLIC;

