SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_quantidade_chamada_pa (nr_atendimento_p bigint) RETURNS bigint AS $body$
DECLARE


qt_total_chamada_w	bigint;
senha_w bigint;


BEGIN

select max(nr_seq_pac_senha_fila)
	into STRICT senha_w
	from atendimento_paciente
	where nr_atendimento = nr_atendimento_p;

if (coalesce(senha_w::text, '') = '') then
	select 	count(*)
	into STRICT 	qt_total_chamada_w
	from 	log_chamada_pa a
	where 	a.nr_atendimento = nr_atendimento_p;

else
    select coalesce(max(QT_CHAMADAS),0)
    into STRICT qt_total_chamada_w
    from paciente_senha_fila
    where (dt_chamada IS NOT NULL AND dt_chamada::text <> '')
    and nr_sequencia = senha_w;
end if;

return	qt_total_chamada_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_quantidade_chamada_pa (nr_atendimento_p bigint) FROM PUBLIC;

