SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_quantidade_conf_receb ( nr_ordem_compra_p bigint, nr_item_oci_p bigint, nr_seq_entrega_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


/* ie_opcao_p
S - Saldo (Quantidade do item menos inspecionado)
I - Quantidade inspecionada
*/
qt_material_w	double precision;
qt_item_oci_w	double precision;
qt_inspecao_w	double precision;


BEGIN

select	qt_prevista_entrega
into STRICT	qt_item_oci_w
from	ordem_compra_item_entrega
where	nr_sequencia = nr_seq_entrega_p;

select	sum(CASE WHEN coalesce(qt_conferencia,0)=0 THEN qt_material  ELSE qt_conferencia END )
into STRICT	qt_inspecao_w
from	ordem_compra_item_conf
where	nr_ordem_compra = nr_ordem_compra_p
and	nr_item_oci = nr_item_oci_p
and	nr_seq_entrega = nr_seq_entrega_p
and	coalesce(dt_estorno::text, '') = '';

if (ie_opcao_p = 'S') then
	qt_material_w := coalesce(qt_item_oci_w,0) - coalesce(qt_inspecao_w,0);
elsif (ie_opcao_p = 'I') then
	qt_material_w := coalesce(qt_inspecao_w,0);
end if;

return qt_material_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_quantidade_conf_receb ( nr_ordem_compra_p bigint, nr_item_oci_p bigint, nr_seq_entrega_p bigint, ie_opcao_p text) FROM PUBLIC;

