CREATE OR REPLACE FUNCTION obter_quantidade_dose_real (dt_inicial_p timestamp, dt_final_p timestamp, cd_material_p bigint) RETURNS bigint AS $body$
DECLARE


qt_dose_real_w	double precision := 0;


BEGIN
	IF (cd_material_p IS NOT NULL AND cd_material_p::text <> '') THEN
		SELECT SUM(coalesce(qt_dose_real,0))
		INTO STRICT	qt_dose_real_w
		FROM   can_ordem_prod_mat
		WHERE  cd_material	= cd_material_p
		AND	   dt_atualizacao BETWEEN coalesce(dt_inicial_p,clock_timestamp()) AND fim_dia(coalesce(dt_final_p,clock_timestamp()));
		END IF;


RETURN	qt_dose_real_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
