CREATE OR REPLACE FUNCTION obter_quantidade_executada (dt_inicial_p timestamp, dt_final_p timestamp, cd_material_p bigint) RETURNS bigint AS $body$
DECLARE


qt_executada_w	double precision := 0;


BEGIN
	IF (cd_material_p IS NOT NULL AND cd_material_p::text <> '') THEN
	SELECT SUM(coalesce(qt_executada,0))
	INTO STRICT   qt_executada_w
	FROM   material_atend_paciente
	WHERE  cd_material	= cd_material_p
	AND	   dt_atendimento BETWEEN coalesce(dt_inicial_p,clock_timestamp()) AND fim_dia(coalesce(dt_final_p,clock_timestamp()))
	AND	   (nr_seq_ordem_prod IS NOT NULL AND nr_seq_ordem_prod::text <> '');
	END IF;


RETURN	qt_executada_w;

END;
