CREATE OR REPLACE FUNCTION obter_quantidade_intervalos_atx ( ds_horarios_p text) RETURNS bigint AS $body$
DECLARE


nr_intervalo_w		bigint:=1;
ds_horarios_w		varchar(2000);
ie_contador_w		bigint:=0;
tam_lista_w		bigint;
ie_pos_espaco_w		smallint;
BEGIN


ds_horarios_w	:= ds_horarios_p;

while(ds_horarios_w IS NOT NULL AND ds_horarios_w::text <> '') and (ie_contador_w < 100) loop
	begin
	tam_lista_w		:= length(trim(both ds_horarios_w));
	ie_pos_espaco_w		:= position(' ' in trim(both ds_horarios_w));
	if (ie_pos_espaco_w <> 0) then
		nr_intervalo_w	:= nr_intervalo_w + 1;
		ds_horarios_w	:= substr(ds_horarios_w,(ie_pos_espaco_w + 1),tam_lista_w);
	end if;
	ie_contador_w := ie_contador_w + 1;
	end;
end loop;

return	nr_intervalo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
