CREATE OR REPLACE FUNCTION obter_quantidade_mat_setor ( cd_material_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint, cd_setor_atendimento_p bigint, nr_interno_conta_p bigint, ie_proc_mat_p text) RETURNS bigint AS $body$
DECLARE


qt_procedimento_w	double precision;
qt_material_w		double precision;


BEGIN

if (cd_material_p IS NOT NULL AND cd_material_p::text <> '') then

	select	coalesce(sum(qt_material),0)
	into STRICT	qt_material_w
	from	material_atend_paciente
	where	nr_interno_conta	= nr_interno_conta_p
	and	coalesce(cd_motivo_exc_conta::text, '') = ''
	and	cd_material		= cd_material_p
	and	cd_setor_atendimento	= cd_setor_atendimento_p;

elsif (cd_procedimento_p IS NOT NULL AND cd_procedimento_p::text <> '') and (ie_origem_proced_p IS NOT NULL AND ie_origem_proced_p::text <> '') then

	select	coalesce(sum(qt_procedimento),0)
	into STRICT	qt_procedimento_w
	from	procedimento_paciente
	where	nr_interno_conta	= nr_interno_conta_p
	and	coalesce(cd_motivo_exc_conta::text, '') = ''
	and	cd_procedimento		= cd_procedimento_p
	and	ie_origem_proced	= ie_origem_proced_p
	and	cd_setor_atendimento	= cd_setor_atendimento_p;
end if;

if (ie_proc_mat_p	= 'P') then
	return coalesce(qt_procedimento_w,0);
elsif (ie_proc_mat_p	= 'M') then
	return coalesce(qt_material_w,0);
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
