SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_quantidade_preceptor (cd_pessoa_estudante_p bigint) RETURNS bigint AS $body$
DECLARE

								
vl_retorno_w integer := 0;


BEGIN

if (get_if_rule_preceptor(cd_pessoa_estudante_p) = 'S') then
	--person
	select count(a.nr_sequencia)
	into STRICT vl_retorno_w
	from pessoa_fisica_preceptor a, pessoa_fisica_estudante b
	where a.cd_pessoa_estudante = cd_pessoa_estudante_p
	and b.cd_pessoa_fisica = a.cd_pessoa_estudante
	and b.ie_situacao = 'A'
	and clock_timestamp() between coalesce(dt_inicio,clock_timestamp()) and coalesce(dt_fim,clock_timestamp());

else
	--profile
	select count(a.nr_sequencia)
	into STRICT vl_retorno_w
	from regra_preceptor_pep a, regra_preceptor_pep_prof b
	where b.nr_seq_regra = a.nr_sequencia
	and a.cd_perfil = coalesce(obter_perfil_ativo,0)
	and a.ie_situacao = 'A'
	and	clock_timestamp() between coalesce(dt_inicio,clock_timestamp()) and coalesce(dt_fim,clock_timestamp());
	
end if;	
	
return vl_retorno_w;
	
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_quantidade_preceptor (cd_pessoa_estudante_p bigint) FROM PUBLIC;

