CREATE OR REPLACE FUNCTION obter_quantidade_solic_agrup ( nr_cot_compra_p bigint, nr_item_cot_compra_p bigint) RETURNS bigint AS $body$
DECLARE

			
qt_material_w	cot_compra_solic_agrup.qt_material%type;
			

BEGIN

select 	sum(c.qt_material)
into STRICT	qt_material_w
from	cot_compra_solic_agrup c,
	solic_compra a,
	solic_compra_item b
where	a.nr_solic_compra = b.nr_solic_compra
and	c.nr_solic_compra = b.nr_solic_compra
and	c.nr_item_solic_compra = b.nr_item_solic_compra
and	c.nr_cot_compra = nr_cot_compra_p
and	c.nr_item_cot_compra = nr_item_cot_compra_p
and	coalesce(a.cd_motivo_baixa::text, '') = ''
and	coalesce(b.cd_motivo_baixa::text, '') = '';

return	qt_material_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
