CREATE OR REPLACE FUNCTION obter_quant_acomp_atendimento (nr_atendimento_p bigint, cd_pessoa_fisica_p text) RETURNS bigint AS $body$
DECLARE


qt_acompanhante_w			varchar(255);


BEGIN

SELECT	count(*)
into STRICT	qt_acompanhante_w
FROM	atendimento_acompanhante
WHERE	nr_atendimento	= nr_atendimento_p
and	((cd_pessoa_fisica <> cd_pessoa_fisica_p) or (coalesce(cd_pessoa_fisica::text, '') = ''))
AND	((cd_pessoa_fisica IS NOT NULL AND cd_pessoa_fisica::text <> '') OR (nm_acompanhante IS NOT NULL AND nm_acompanhante::text <> ''))
AND	coalesce(dt_saida::text, '') = '';


RETURN qt_acompanhante_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
