CREATE OR REPLACE FUNCTION obter_quant_reserva_acomod ( cd_tipo_acomodacao_p bigint, dt_agendamento_p timestamp, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
qt_regra_w		bigint;
ie_dia_da_semana_w	varchar(1);
qt_reserva_w		bigint;


BEGIN 
 
IF (cd_tipo_acomodacao_p IS NOT NULL AND cd_tipo_acomodacao_p::text <> '') THEN 
	ie_dia_da_semana_w := Obter_Cod_Dia_Semana(dt_agendamento_p);
	SELECT	COUNT(*) 
	INTO STRICT	qt_regra_w 
	FROM	regra_reserva_vaga_acomod 
	WHERE	cd_tipo_acomodacao 	= cd_tipo_acomodacao_p 
	AND	ie_dia_semana		= ie_dia_da_semana_w 
	AND 	((cd_estabelecimento = cd_estabelecimento_p) OR (coalesce(cd_estabelecimento::text, '') = ''));
 
	IF (qt_regra_w > 0) THEN 
		SELECT	COUNT(*) 
		INTO STRICT	qt_reserva_w 
		FROM	gestao_vaga 
		WHERE	dt_agendamento_p BETWEEN trunc(dt_prevista) AND fim_dia(dt_prevista) 
		AND	cd_tipo_acomod_desej = cd_tipo_acomodacao_p 
		AND 	((cd_estabelecimento = cd_estabelecimento_p) OR (coalesce(cd_estabelecimento::text, '') = '' ));
	END IF;
END IF;
 
RETURN	qt_reserva_w;
 
END	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
