CREATE OR REPLACE FUNCTION obter_qua_tipo_acao_usuario ( nm_usuario_p text, nr_seq_tipo_p bigint, ie_tipo_p text) RETURNS varchar AS $body$
DECLARE


ie_registrar_w		varchar(1);
ie_causa_acao_w		varchar(1);
ie_definir_eficacia_w	varchar(1);
ie_cancelar_w		varchar(1);
ie_fechar_w		varchar(1);
ds_retorno_w		varchar(1);
cd_perfil_w		integer;
cd_perfil_ww		integer;
nm_usuario_lib_ww		varchar(15);
ie_visualizar_w		varchar(01) := 'S';

/* ie_tipo
NC - Abrir Não Conformidade
CA - Abrir Causas e Ações
DE - Definir Eficácia
PC - Permite Cancelar
PF - Permite Fechar
PV - Permite Visualizar
*/
C01 CURSOR FOR
	SELECT	coalesce(ie_registrar,'N'),
		coalesce(ie_causa_acao,'N'),
		coalesce(ie_definir_eficacia,'N'),
		coalesce(ie_cancelar,'N'),
		coalesce(ie_fechar,'N'),
		cd_perfil,
		nm_usuario_lib,
		coalesce(ie_visualizar,'S')
	from	qua_tipo_nconf_usuario
	where	nm_usuario_lib		= nm_usuario_p
	and	nr_seq_nconform		= nr_seq_tipo_p
	
union

	SELECT	coalesce(ie_registrar,'N'),
		coalesce(ie_causa_acao,'N'),
		coalesce(ie_definir_eficacia,'N'),
		coalesce(ie_cancelar,'N'),
		coalesce(ie_fechar,'N'),
		cd_perfil,
		nm_usuario_lib,
		coalesce(ie_visualizar,'S')
	from	qua_tipo_nconf_usuario
	where	nr_seq_nconform		= nr_seq_tipo_p
	and     cd_perfil			= cd_perfil_w
	order by cd_perfil,
		nm_usuario_lib;


BEGIN

cd_perfil_w := obter_perfil_ativo;

open c01;
loop
fetch c01 into
	ie_registrar_w,
	ie_causa_acao_w,
	ie_definir_eficacia_w,
	ie_cancelar_w,
	ie_fechar_w,
	cd_perfil_ww,
	nm_usuario_lib_ww,
	ie_visualizar_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	if (ie_tipo_p = 'NC') then
		ds_retorno_w	:= ie_registrar_w;
	elsif (ie_tipo_p = 'CA') then
		ds_retorno_w	:= ie_causa_acao_w;
	elsif (ie_tipo_p = 'DE') then
		ds_retorno_w	:= ie_definir_eficacia_w;
	elsif (ie_tipo_p = 'PC') then
		ds_retorno_w	:= ie_cancelar_w;
	elsif (ie_tipo_p = 'PF') then
		ds_retorno_w	:= ie_fechar_w;
	elsif (ie_tipo_p = 'PV') then
		ds_retorno_w	:= ie_visualizar_w;
	end if;
	end;
end loop;
close c01;

return	ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
