CREATE OR REPLACE FUNCTION obter_queixa_triagem ( nr_atendimento_p atendimento_paciente.nr_atendimento%type, nr_seq_queixa_p queixa_paciente.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);
				

BEGIN

if (coalesce(pkg_i18n.get_user_locale, 'pt_BR') in ('de_DE', 'de_AT')) then
	select	max(tpa.ds_queixa_princ)
	into STRICT	ds_retorno_w
	from	triagem_pronto_atend tpa
	where	tpa.nr_sequencia = (	SELECT	max(nr_sequencia)
									from	triagem_pronto_atend tp
									where	tpa.nr_atendimento = tp.nr_atendimento
									and		tp.nr_atendimento = nr_atendimento_p);
else
	select	substr(Obter_Queixas_Atendimento(nr_atendimento_p, nr_seq_queixa_p),1,255)
	into STRICT	ds_retorno_w
	;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
