SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_quilogramas (qt_gramas_p bigint, ie_mascara_p text) RETURNS varchar AS $body$
DECLARE


/*
ie_mascara_p
	'KGG': 1500 = 1 kg 500 g

*/
ds_resultado_w	varchar(255)	:= '';


BEGIN

if (ie_mascara_p = 'KGG') then
	if (qt_gramas_p < 1000) then
		ds_resultado_w	:= to_char(qt_gramas_p) || ' g';
	elsif (mod(qt_gramas_p, 1000) = 0) then
		ds_resultado_w	:= trunc(qt_gramas_p / 1000) || ' kg';
	else
		ds_resultado_w	:=  trunc(qt_gramas_p / 1000) || ' kg ' || mod(qt_gramas_p, 1000) || ' g';
	end if;
end if;

return	ds_resultado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
