CREATE OR REPLACE FUNCTION obter_range_medicamento (ds_material text, pattern text) RETURNS varchar AS $body$
BEGIN
return position(substr(pattern, 2, length(pattern) - 2) in ds_material) || '-' ||  (LENGTH(pattern) - 2);
exception
    when others then
    return '0-0';
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 IMMUTABLE;
