CREATE OR REPLACE FUNCTION obter_reac_coleta_anterior ( cd_pessoa_fisica_p text , nr_sequencia_p bigint ) RETURNS varchar AS $body$
DECLARE


ie_contem_anterior_W 			bigint;
ie_reacao_coleta_anterior_w		varchar(1);


BEGIN

SELECT	COUNT(*)
INTO STRICT	ie_contem_anterior_W
FROM 	SAN_DOACAO_REACAO a ,san_doacao b
WHERE 	b.cd_pessoa_fisica	= cd_pessoa_fisica_p
AND 	a.nr_seq_doacao 		<> nr_sequencia_p
AND 	a.nr_seq_doacao = b.nr_sequencia;

IF ( ie_contem_anterior_W = 0 ) THEN
      ie_reacao_coleta_anterior_w	:=  'N';
ELSE
     ie_reacao_coleta_anterior_w	:=  'S';
END IF;

RETURN ie_reacao_coleta_anterior_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
