CREATE OR REPLACE FUNCTION obter_readonly_grafico_html5 (nm_usuario_p text, nr_cirurgia_p bigint, nr_seq_pepo_p bigint, ie_fanep_p text default 'N') RETURNS varchar AS $body$
DECLARE

												
ie_read_only_w 		varchar(1) :=  'N';
ie_altera_grafico_w 	varchar(1) :=  'N';
ie_reg_canc_interr_w varchar(1) :=  'N';


dt_liberacao_w timestamp;
dt_liberacao_anestesista_w timestamp;
dt_liberacao_pepo_w timestamp;



BEGIN
ie_altera_grafico_w := Obter_Param_Usuario(872, 18, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_altera_grafico_w);
ie_reg_canc_interr_w := Obter_Param_Usuario(872, 119, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_reg_canc_interr_w);


if (nr_cirurgia_p IS NOT NULL AND nr_cirurgia_p::text <> '') then
	select	max(dt_liberacao),
				max(dt_liberacao_anestesista)
	into STRICT		dt_liberacao_w ,
				dt_liberacao_anestesista_w
	from		cirurgia
	where		nr_cirurgia = nr_cirurgia_p;
end if;

if (nr_seq_pepo_p IS NOT NULL AND nr_seq_pepo_p::text <> '') then
	select	max(dt_liberacao)
	into STRICT     dt_liberacao_pepo_w
	from		pepo_cirurgia
	where		nr_sequencia = nr_seq_pepo_p;
end if;

if (ie_altera_grafico_w = 'N') or (dt_liberacao_w IS NOT NULL AND dt_liberacao_w::text <> '') or (dt_liberacao_anestesista_w IS NOT NULL AND dt_liberacao_anestesista_w::text <> '') or (dt_liberacao_pepo_w IS NOT NULL AND dt_liberacao_pepo_w::text <> '') then
	ie_read_only_w := 'S';
end if;

if (ie_read_only_w = 'N') and (ie_reg_canc_interr_w = 'N') and (ie_fanep_p = 'N') then
	
	select	coalesce(max('S'),'N')
	into STRICT 		ie_read_only_w
	from		cirurgia
	where		nr_cirurgia = nr_cirurgia_p
	and 		ie_status_cirurgia in (3,4);
end if;

if (dt_liberacao_pepo_w IS NOT NULL AND dt_liberacao_pepo_w::text <> '') and (ie_fanep_p = 'S') then

  ie_read_only_w := 'S';
end if;

return	ie_read_only_w;

end;
$body$
