CREATE OR REPLACE FUNCTION obter_recomendacao_medic (nr_sequencia_p bigint, nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_recomendacao_w	varchar(4000);


BEGIN

select	max(obter_rec_prescricao(nr_sequencia, nr_prescricao))
into STRICT	ds_recomendacao_w
from	prescr_recomendacao
where	nr_sequencia	= nr_sequencia_p
and	nr_prescricao	= nr_prescricao_p
and	ie_suspenso	<> 'S';

return	ds_recomendacao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
