CREATE OR REPLACE FUNCTION obter_reconciliacao_atend ( nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1) := 'N';

/*
ds_retorno_w
N - Não possui nenhum registro de medicamentos em uso para o atendimento
M - Há registros de medicamentos em uso
R - Todos os medicamentos estão reconciliados
A - Existem medicamentos que não possuem reconciliação
*/
BEGIN

if (nr_atendimento_p > 0) then
	select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'M' END
	into STRICT	ds_retorno_w
	from	paciente_medic_uso
	where	nr_atendimento = nr_atendimento_p
	and	coalesce(dt_inativacao::text, '') = ''
	and	coalesce(ie_nega_medicamentos,'N') = 'N';

	if (ds_retorno_w = 'M') then
		select	CASE WHEN count(*)=0 THEN 'R'  ELSE 'A' END
		into STRICT	ds_retorno_w
		from	paciente_medic_uso a
		where	a.nr_atendimento = nr_atendimento_p
		and	coalesce(a.dt_inativacao::text, '') = ''
		and	not exists (	SELECT	1
					from 	hist_saude_reconciliacao b
					where 	b.nr_seq_pac_hist = a.nr_sequencia);
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
