CREATE OR REPLACE FUNCTION obter_recurso_projeto ( nr_seq_projeto_p bigint, nr_seq_tipo_equipe_p bigint, nr_seq_tipo_papel_p bigint, ie_funcao_migr_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

		 
nr_seq_recurso_w	bigint;
cd_recurso_w		varchar(10);
ds_retorno_w		varchar(60) := null;
		

BEGIN 
if (nr_seq_projeto_p IS NOT NULL AND nr_seq_projeto_p::text <> '') and (nr_seq_tipo_equipe_p IS NOT NULL AND nr_seq_tipo_equipe_p::text <> '') and (nr_seq_tipo_papel_p IS NOT NULL AND nr_seq_tipo_papel_p::text <> '') then 
	begin 
	select	coalesce(max(i.nr_sequencia),0) 
	into STRICT	nr_seq_recurso_w 
	from	proj_equipe e, 
		proj_equipe_papel i 
	where	e.nr_sequencia = i.nr_seq_equipe 
	and	e.nr_seq_proj = nr_seq_projeto_p 
	and	e.nr_seq_equipe_funcao = nr_seq_tipo_equipe_p 
	and	i.nr_seq_funcao = nr_seq_tipo_papel_p 
	and	((coalesce(ie_funcao_migr_p::text, '') = '') or (i.ie_funcao_rec_migr = ie_funcao_migr_p)) 
	and	(i.cd_pessoa_fisica IS NOT NULL AND i.cd_pessoa_fisica::text <> '') 
	and	coalesce(i.ie_situacao,'A') = 'A';
	 
	if (nr_seq_recurso_w > 0) then 
		begin 
		select	max(cd_pessoa_fisica) 
		into STRICT	cd_recurso_w 
		from	proj_equipe_papel 
		where	nr_sequencia = nr_seq_recurso_w;
		 
		if (cd_recurso_w IS NOT NULL AND cd_recurso_w::text <> '') then 
			begin 
			if (ie_opcao_p = 'D') then 
				begin 
				ds_retorno_w := obter_nome_pf(cd_recurso_w);
				end;
			else 
				begin 
				ds_retorno_w := cd_recurso_w;
				end;
			end if;
			end;
		end if;
		end;
	end if;
	end;
end if;
return ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
