CREATE OR REPLACE FUNCTION obter_rec_glosa_ret ( nr_seq_retorno_p bigint) RETURNS varchar AS $body$
DECLARE

ie_recurso_w	varchar(1) := 'N';

BEGIN

begin
select	'S'
into STRICT	ie_recurso_w
from	convenio_retorno c,
	convenio_retorno_item i,
	lote_audit_hist_guia g
where	c.nr_sequencia 	   = i.nr_seq_retorno
and	i.nr_interno_conta = g.nr_interno_conta
and	i.cd_autorizacao   = g.cd_autorizacao
and	c.nr_sequencia	   = nr_seq_retorno_p  LIMIT 1;
exception
when others then
	ie_recurso_w := 'N';
end;

return	ie_recurso_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
