CREATE OR REPLACE FUNCTION obter_rec_prescricao ( NR_SEQUENCIA_P bigint, NR_PRESCRICAO_P bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(4000) := '';
ds_recomendacao_w	varchar(4000) := '';
ds_tipo_recomend_w	varchar(4000) := '';


c01 CURSOR FOR
	SELECT a.ds_recomendacao,
		b.ds_tipo_recomendacao
	FROM prescr_recomendacao a
LEFT OUTER JOIN tipo_recomendacao b ON (a.cd_recomendacao = b.cd_tipo_recomendacao)
WHERE a.nr_prescricao	= NR_PRESCRICAO_P and a.nr_sequencia 	= NR_SEQUENCIA_P;



BEGIN


open	c01;
loop
fetch	c01 into
	ds_recomendacao_w,
	ds_tipo_recomend_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	if (coalesce(ds_tipo_recomend_w::text, '') = '') then
		ds_retorno_w := substr(ds_recomendacao_w,1,4000);
	elsif (coalesce(ds_recomendacao_w::text, '') = '') then
		ds_retorno_w := substr(ds_tipo_recomend_w,1,4000);
	else
		ds_retorno_w := substr(ds_tipo_recomend_w || ' ' || chr(13) || chr(10) ||' ' ||ds_recomendacao_w,1,4000);

	end if;
	end;

end loop;
close c01;


RETURN substr(ds_retorno_w,1,4000);

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
