CREATE OR REPLACE FUNCTION obter_refeicao_obs_tec (nr_atendimento_p bigint, dt_dieta_p timestamp) RETURNS varchar AS $body$
DECLARE

			 
ds_refeicao_w		varchar(4000);
ds_retorno_w		varchar(2000);
ie_ordem_w 		smallint;

C01 CURSOR FOR 
	SELECT 	distinct a.ds_refeicao || ': ' || a.ds_observacao_tec, 
	CASE WHEN a.cd_refeicao='D' THEN 1 WHEN a.cd_refeicao='B' THEN 2 WHEN a.cd_refeicao='A' THEN 3 WHEN a.cd_refeicao='T' THEN 4 WHEN a.cd_refeicao='J' THEN 5 WHEN a.cd_refeicao='C' THEN 6  ELSE 7 END  ie_ordem 
	from mapa_dieta_v a 
	where  a.nr_atendimento = nr_atendimento_p 
	and trunc(a.dt_dieta) = dt_dieta_p 
	and coalesce(a.nr_seq_superior::text, '') = '' 
	and a.ie_destino_dieta = 'P' 
	order by ie_ordem;


BEGIN 
open c01;
loop 
fetch c01 into 
 
	ds_refeicao_w, 
	ie_ordem_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
 begin 
	ds_retorno_w := ds_retorno_w||ds_refeicao_w||chr(13);
	end;
end loop;
close c01;
 
return ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
