CREATE OR REPLACE FUNCTION obter_referencia_escala ( ie_escala_p bigint) RETURNS varchar AS $body$
DECLARE

DS_REFERENCIA_w	varchar(4000);


BEGIN 
 
select	max(obter_desc_expressao(CD_EXP_REF_BIBLI)) 
into STRICT	ds_referencia_w 
from	escala_documentacao 
where	ie_escala = ie_escala_p;
 
 
return	ds_referencia_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
