CREATE OR REPLACE FUNCTION obter_referencia_far_com (cd_codigo_p text, ie_sistema_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);


BEGIN
select	coalesce(cd_referencia, wheb_mensagem_pck.get_texto(799670))
into STRICT	ds_retorno_w
from	material_sistema_externo
where	cd_codigo = cd_codigo_p
and	ie_sistema = ie_sistema_p;

return ds_retorno_w;

end;
$body$
