SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_ref_prescr ( nr_prescricao_p bigint, nr_seq_exame_p bigint, nr_seq_material_p bigint) RETURNS varchar AS $body$
DECLARE


qt_dias_w	double precision	:= 0;
qt_horas_w	bigint	:= 0;
qt_minima_w	double precision;
qt_maxima_w	double precision;
pr_minimo_w	double precision;
pr_maximo_w	double precision;
ds_observacao_w	varchar(2000);
ds_refer_w	varchar(2000);
ie_sexo_w	varchar(1);
cd_estabelecimento_W	prescr_medica.cd_estabelecimento%type;
nr_seq_metodo_w	metodo_exame_lab.nr_sequencia%type;
qt_casas_decimais_dias_w	bigint := 2;


BEGIN

select	obter_dias_entre_datas_lab(obter_nascimento_prescricao(nr_prescricao_p),clock_timestamp()),
	substr(obter_sexo_prescricao(nr_prescricao_p),1,1),
	Obter_Hora_Entre_datas(obter_nascimento_prescricao(nr_prescricao_p),clock_timestamp())
into STRICT	qt_dias_w,
	ie_sexo_w,
	qt_horas_w
;

begin

select 	obter_metodo_regra_material(nr_prescricao_p,nr_seq_exame_p, nr_seq_material_p)
into STRICT	nr_seq_metodo_w
;

exception
	when others then
		nr_seq_metodo_w := null;
end;


select	coalesce(max(cd_estabelecimento),0)
into STRICT	cd_estabelecimento_w
from 	prescr_medica
where	nr_prescricao = nr_prescricao_p;

select	CASE WHEN coalesce(max(ie_idade_int_val_ref), 'N')='N' THEN  2  ELSE 0 END
into STRICT	qt_casas_decimais_dias_w
from	lab_parametro
where	cd_estabelecimento = cd_estabelecimento_w;

select 	qt_minima,
	qt_maxima,
	qt_percent_min,
	qt_percent_max,
	ds_observacao
into STRICT	qt_minima_w,
	qt_maxima_w,
	pr_minimo_w,
	pr_maximo_w,
	ds_observacao_w
from (
	SELECT 	qt_minima,
		qt_maxima,
		qt_percent_min,
		qt_percent_max,
		ds_observacao
	from 	exame_lab_padrao
	where 	((ie_sexo = ie_sexo_w) or (ie_sexo = '0'))
	and 	nr_seq_exame = nr_seq_exame_p
	and 	coalesce(nr_seq_material,nr_seq_material_p) = nr_seq_material_p
	and coalesce(nr_seq_metodo, coalesce(nr_seq_metodo_w,0)) = coalesce(nr_seq_metodo_w,0)
	and 	(((trunc((qt_dias_w / 365.25),qt_casas_decimais_dias_w) between qt_idade_min and qt_idade_max) and (ie_periodo = 'A')) or
		((trunc(((qt_dias_w / 365.25) * 12),qt_casas_decimais_dias_w) between qt_idade_min and qt_idade_max) and (ie_periodo = 'M')) or
		(qt_dias_w between qt_idade_min and qt_idade_max AND ie_periodo = 'D') or
		(qt_horas_w between qt_idade_min and qt_idade_max AND ie_periodo = 'H'))
	and 	ie_tipo_valor in (0,3)
	and coalesce(ie_situacao,'A') = 'A'
	order by coalesce(nr_seq_material, 9999999999), coalesce(nr_seq_metodo, 9999999999), ie_sexo, CASE WHEN ie_periodo='D' THEN 1 WHEN ie_periodo='M' THEN 2  ELSE 3 END ) alias25 LIMIT 1;


if (qt_minima_w >= 0) and (qt_maxima_w > 0) then
	ds_refer_w := (qt_minima_w)::numeric  || ' a '|| (qt_maxima_w)::numeric;
elsif (pr_minimo_w >= 0) and (pr_maximo_w > 0) then
	ds_refer_w := (pr_minimo_w)::numeric  || ' a '|| (pr_maximo_w)::numeric;
else
	ds_refer_w := ds_observacao_w;
end if;

return	ds_refer_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
