CREATE OR REPLACE FUNCTION obter_ref_trat_onc ( ie_modalidade_trat_p text, nr_seq_loco_regional_p bigint, cd_pessoa_fisica_p bigint, cd_protocolo_p bigint default null, nr_seq_medicacao_p bigint default null) RETURNS bigint AS $body$
DECLARE

cd_topografia_w		varchar(10);
cd_estadiamento_w	varchar(10);
ie_retorno_w		varchar(10)	:= 'S';
qt_reg_w		bigint;
nr_seq_referencia_w	bigint;

BEGIN

select	min(nr_seq_referencia)
into STRICT	nr_seq_referencia_w
from	can_loco_regional a,
	cido_topografia b,
	PROT_MEDIC_CAT_TOPOGRAFICA c
where	a.cd_pessoa_fisica	= cd_pessoa_fisica_p
and	b.cd_topografia		= a.CD_TOPOGRAFIA
and	c.cd_categoria		= b.cd_categoria
and	a.nr_sequencia 		= nr_seq_loco_regional_p
and	coalesce(c.ie_situacao,'A')	= 'A'
and	coalesce(ie_modalidade_trat,coalesce(ie_modalidade_trat_p,'XPTO'))	= coalesce(ie_modalidade_trat_p,'XPTO')
and	coalesce(c.cd_protocolo,cd_protocolo_p)	= coalesce(cd_protocolo_p,c.cd_protocolo)
and	coalesce(c.NR_SEQUENCIA,nr_seq_medicacao_p)	= coalesce(nr_seq_medicacao_p,c.NR_SEQUENCIA);

return	nr_seq_referencia_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
