SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_regiao_pf_heg ( cd_pessoa_fisica_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
  IE_OPCAO:	 C -> Código
                 D -> Descrição

*/
nr_cep_reg_metropo_w	varchar(255)	:= '(410020,410030,410040,410180,410230,410310,410400,410420,410425,410520,410580,410620,412863,410765,411125,411320,411430,411915,411950,412080,412120,412220,412550,412760,412788)';
nr_cep_capital_w	varchar(6)	:= '410690';
sg_estado_w		varchar(15)	:= 'PR';
ie_result_w		varchar(1)	:= '';

nr_cep_pf_w		varchar(6);
sg_estado_pf_w		varchar(15);

ds_resultado_w		varchar(20);


BEGIN

/* Verifica o código do município da Pessoa Física */

begin
select	a.cd_municipio_ibge,
	b.ds_unidade_federacao
into STRICT	nr_cep_pf_w,
	sg_estado_pf_w
from	sus_municipio b,
	compl_pessoa_fisica a
where	b.cd_municipio_ibge = a.cd_municipio_ibge
and	cd_pessoa_fisica	= cd_pessoa_fisica_p
and	ie_tipo_complemento	= 1;
exception
	when others then
		nr_cep_pf_w	:= null;
		sg_estado_pf_w	:= null;
end;

if (nr_cep_pf_w IS NOT NULL AND nr_cep_pf_w::text <> '') then --CEP Nulo
	if (sg_estado_pf_w = sg_estado_w) then --Estados diferentes
		if (nr_cep_pf_w <> nr_cep_capital_w) then --Capital
			if (obter_se_contido(somente_numero(nr_cep_pf_w), nr_cep_reg_metropo_w) = 'S') then --Região metropolitana
				ie_result_w := 'M';
			else
				ie_result_w := 'I';
			end if;
		else
			ie_result_w := 'C';
		end if;
	else
		ie_result_w := 'O';
	end if;
else
	ie_result_w := 'N';
end if;


if (ie_opcao_p  = 'D') then
	if (ie_result_w = 'C') then
		ds_resultado_w := wheb_mensagem_pck.get_texto(309258); -- Capital
	elsif (ie_result_w = 'M') then
		ds_resultado_w := wheb_mensagem_pck.get_texto(309259); -- Região Metropolitana
	elsif (ie_result_w = 'I') then
		ds_resultado_w := wheb_mensagem_pck.get_texto(309260); -- Interior
	elsif (ie_result_w = 'O') then
		ds_resultado_w := wheb_mensagem_pck.get_texto(309261); -- Outros estados
	elsif (ie_result_w = 'N') then
		ds_resultado_w := wheb_mensagem_pck.get_texto(309262); -- Região não informada
	end if;
else
	ds_resultado_w := ie_result_w;
end if;



return ds_resultado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
