CREATE OR REPLACE FUNCTION obter_regiao_uf (sg_uf_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
sg_uf_w		varchar(2);


BEGIN

if (sg_uf_p IS NOT NULL AND sg_uf_p::text <> '') then
	begin

	sg_uf_w := upper(sg_uf_p);

	if (sg_uf_w in ('RS','SC','PR')) then
		ds_retorno_w := wheb_mensagem_pck.get_texto(309256); -- Sul
	elsif (sg_uf_w in ('SP','MG','RJ','ES')) then
		ds_retorno_w := wheb_mensagem_pck.get_texto(309255); -- Sudeste
	elsif (sg_uf_w in ('MT','MS','GO','DF')) then
		ds_retorno_w := wheb_mensagem_pck.get_texto(309254); -- Centro-Oeste
	elsif (sg_uf_w in ('AC','AM','RO','RR','AP','PA','TO')) then
		ds_retorno_w := wheb_mensagem_pck.get_texto(309253); -- Norte
	elsif (sg_uf_w in ('MA','PI','BA','CE','RN','PB','PE','AL','SE')) then
		ds_retorno_w := wheb_mensagem_pck.get_texto(309252); -- Nordeste
	end if;
	end;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
