SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_registro_insp_devol (nr_seq_registro_p bigint) RETURNS varchar AS $body$
DECLARE



qt_inspecao_w			bigint;
qt_devolvidos_w			bigint;
ie_status_w			varchar(15) := 'ND';


BEGIN

select	count(*)
into STRICT	qt_inspecao_w
from	inspecao_recebimento
where	nr_seq_registro = nr_seq_registro_p;

select	count(*)
into STRICT	qt_devolvidos_w
from	inspecao_recebimento
where	nr_seq_registro = nr_seq_registro_p
and	(ie_motivo_devolucao IS NOT NULL AND ie_motivo_devolucao::text <> '');

if (qt_inspecao_w > 0) and (qt_devolvidos_w > 0) and (qt_inspecao_w = qt_devolvidos_w) then
	ie_status_w	:= 'TD'; /*Tudo devolvido*/
elsif (qt_inspecao_w > 0) and (qt_devolvidos_w > 0) and (qt_inspecao_w > qt_devolvidos_w) then
	ie_status_w	:= 'DP'; /*Devolução parcial*/
elsif (qt_inspecao_w > 0) and (qt_devolvidos_w = 0) then
	ie_status_w	:= 'ND'; /*Nenhuma devolução*/
end if;


return	ie_status_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
