CREATE OR REPLACE FUNCTION obter_regras_atrib_filtro ( nr_seq_wfiltro_p wfiltro_atrib_regra.nr_seq_wfiltro%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type, cd_perfil_p perfil.cd_perfil%type) RETURNS bigint AS $body$
DECLARE


nr_seq_retorno_w	wfiltro_atrib_regra.nr_sequencia%type;
count_w	integer	:= 0;


BEGIN

select	count(*)
into STRICT		count_w
from		wfiltro_atrib_regra
where	nr_seq_wfiltro = nr_seq_wfiltro_p;

if (count_w > 0) then
	begin
		select	nr_sequencia
		into STRICT		nr_seq_retorno_w
		from		wfiltro_atrib_regra
		where	nr_seq_wfiltro = nr_seq_wfiltro_p
		and		cd_perfil = cd_perfil_p
		and		cd_estabelecimento	= cd_estabelecimento_p
		and		coalesce(nm_usuario_regra::text, '') = '';
	exception
	when others then
		begin
			select	nr_sequencia
			into STRICT		nr_seq_retorno_w
			from		wfiltro_atrib_regra
			where	nr_seq_wfiltro = nr_seq_wfiltro_p
			and		cd_perfil = cd_perfil_p
			and		coalesce(cd_estabelecimento::text, '') = ''
			and		coalesce(nm_usuario_regra::text, '') = '';
		exception
		when others then
			begin
				select	nr_sequencia
				into STRICT		nr_seq_retorno_w
				from		wfiltro_atrib_regra
				where	nr_seq_wfiltro = nr_seq_wfiltro_p
				and		cd_estabelecimento	= cd_estabelecimento_p
				and		coalesce(cd_perfil::text, '') = ''
				and		coalesce(nm_usuario_regra::text, '') = '';
			exception
			when others then
				begin
					select	nr_sequencia
					into STRICT		nr_seq_retorno_w
					from		wfiltro_atrib_regra
					where	nr_seq_wfiltro = nr_seq_wfiltro_p
					and		coalesce(cd_estabelecimento::text, '') = ''
					and		coalesce(cd_perfil::text, '') = ''
					and		coalesce(nm_usuario_regra::text, '') = '';
				exception
				when others then
					nr_seq_retorno_w	:= 0;	
				end;
			end;
		end;
	end;
end if;

return	nr_seq_retorno_w;

end;
