CREATE OR REPLACE FUNCTION obter_regras_atrib_wcp ( nr_seq_wcpanel_p wcp_regra_atributo.nr_seq_wcpanel%type, nr_seq_atributo_p wcp_regra_atributo.nr_seq_atributo%type, cd_perfil_p perfil.cd_perfil%type) RETURNS bigint AS $body$
DECLARE


nr_seq_retorno_w	wcp_regra_atributo.nr_sequencia%type;
count_w	integer	:= 0;


BEGIN

select	count(*)
into STRICT		count_w
from		wcp_regra_atributo
where	nr_seq_wcpanel = nr_seq_wcpanel_p
and		nr_seq_atributo = nr_seq_atributo_p;

if (count_w > 0) then
	select	max(nr_sequencia)
	into STRICT		nr_seq_retorno_w
	from		wcp_regra_atributo
	where	nr_seq_wcpanel = nr_seq_wcpanel_p
	and		nr_seq_atributo = nr_seq_atributo_p
	and		cd_perfil = cd_perfil_p
	and		coalesce(nm_usuario_regra::text, '') = '';
	
	if (coalesce(nr_seq_retorno_w, 0) = 0) then
		select	max(nr_sequencia)
		into STRICT		nr_seq_retorno_w
		from		wcp_regra_atributo
		where	nr_seq_wcpanel = nr_seq_wcpanel_p
		and		nr_seq_atributo = nr_seq_atributo_p
		and		coalesce(cd_perfil::text, '') = ''
		and		coalesce(nm_usuario_regra::text, '') = '';
		
		if (coalesce(nr_seq_retorno_w, 0) = 0) then
			nr_seq_retorno_w	:= 0;
		end if;
	end if;
end if;

return	nr_seq_retorno_w;

end;
$body$
