CREATE OR REPLACE FUNCTION obter_regras_conv_part ( nr_atendimento_p bigint, cd_convenio_p bigint ) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1);


BEGIN

	SELECT coalesce(max(IE_SOLIC_PLANO_PART),'N')
	into STRICT ds_retorno_w
		FROM PARAMETRO_ATENDIMENTO
		WHERE cd_estabelecimento = obter_estabelecimento_ativo
		AND EXISTS (SELECT 1
			FROM   CONVENIO
			WHERE  CD_CONVENIO = CD_CONVENIO_P
			AND    IE_TIPO_CONVENIO = 3  LIMIT 1)
		AND NOT EXISTS (SELECT 1
			FROM CONVENIO_PARTICULAR_ATEND
			WHERE nr_atendimento = nr_atendimento_p  LIMIT 1) LIMIT 1;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
