DECLARE


nr_seq_retorno_w	tasy_padrao_cor_perfil.nr_sequencia%type;
count_w	integer	:= 0;

c01 CURSOR FOR
	SELECT	max(nr_sequencia) nr_sequencia
	from		tasy_padrao_cor_perfil
	where	nr_seq_padrao = nr_seq_cor_p
	and		cd_perfil = cd_perfil_p
	and		cd_estabelecimento	= cd_estabelecimento_p;

