c02 CURSOR FOR
	SELECT	max(nr_sequencia) nr_sequencia
	from		tasy_padrao_cor_perfil
	where	nr_seq_padrao = nr_seq_cor_p
	and		cd_perfil = cd_perfil_p
	and		coalesce(cd_estabelecimento::text, '') = '';

c02_w		c02%rowtype;

c03 CURSOR FOR
	SELECT	max(nr_sequencia) nr_sequencia
	from		tasy_padrao_cor_perfil
	where	nr_seq_padrao = nr_seq_cor_p
	and		cd_estabelecimento	= cd_estabelecimento_p
	and		coalesce(cd_perfil::text, '') = '';

