	where	nr_seq_padrao = nr_seq_cor_p
	and		coalesce(cd_estabelecimento::text, '') = ''
	and		coalesce(cd_perfil::text, '') = '';

c04_w		c04%rowtype;


BEGIN

select	count(*)
into STRICT		count_w
from		tasy_padrao_cor_perfil
where	nr_seq_padrao = nr_seq_cor_p;

if (count_w > 0) then
	open c01;
	loop
	fetch c01 into
		c01_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
			nr_seq_retorno_w	:= coalesce(c01_w.nr_sequencia, 0);
		end;
	end loop;
	close c01;
	if (nr_seq_retorno_w = 0) then
		open c02;
		loop
		fetch c02 into
			c02_w;
		EXIT WHEN NOT FOUND; /* apply on c02 */
		begin
			nr_seq_retorno_w	:= coalesce(c02_w.nr_sequencia, 0);
		end;
		end loop;
		close c02;
		if (nr_seq_retorno_w = 0) then
			open c03;
