CREATE OR REPLACE FUNCTION obter_regra_abrir_atend ( nr_atendimento_p bigint, ie_opcao_p bigint, nr_interno_conta_p bigint) RETURNS varchar AS $body$
DECLARE

 
ie_permite_w		varchar(1):= 'S';
cd_convenio_w		integer;
ie_tipo_atendimento_w	smallint;
cd_perfil_w		integer;
nr_sequencia_w		bigint;
qt_regra_w		bigint:= 0;
ie_fecha_atendimento_w	varchar(1)	:= 'S';
ie_fecha_conta_w		varchar(1)	:= 'S';
cd_plano_convenio_w	varchar(10);
nr_interno_conta_w		bigint;
nr_seq_etapa_w		bigint;

C01 CURSOR FOR 
	SELECT	ie_fecha_atendimento, 
		ie_fecha_conta 
	from	conv_regra_lib_atend 
	where	cd_convenio = cd_convenio_w 
	and 	coalesce(ie_tipo_atendimento,ie_tipo_atendimento_w) = ie_tipo_atendimento_w 
	and	coalesce(cd_perfil,cd_perfil_w) = cd_perfil_w 
	and 	coalesce(ie_situacao,'A') = 'A' 
	and	coalesce(cd_plano_convenio,coalesce(cd_plano_convenio_w,'0')) = coalesce(cd_plano_convenio_w,'0') 
	and	coalesce(nr_seq_etapa_filtro, coalesce(nr_seq_etapa_w,0)) = coalesce(nr_seq_etapa_w,0) 
	order by	coalesce(cd_perfil, cd_perfil_w), 
		coalesce(ie_tipo_atendimento, ie_tipo_atendimento_w);

		 
C02 CURSOR FOR 
	SELECT	nr_interno_conta 
	from	conta_paciente 
	where	nr_atendimento = nr_atendimento_p 
	and 	((ie_opcao_p = 1) or ((ie_opcao_p = 2) and ((coalesce(nr_interno_conta_p::text, '') = '') or (nr_interno_conta = nr_interno_conta_p)))) 
	order by 1;
	

BEGIN 
 
cd_perfil_w:= obter_perfil_ativo;
 
select 	ie_tipo_atendimento 
into STRICT	ie_tipo_atendimento_w 
from 	atendimento_paciente 
where 	nr_atendimento = nr_atendimento_p;
 
select	/*+ index (a ATECACO_I3) */	 
	cd_convenio, 
	cd_plano_convenio    
into STRICT	cd_convenio_w, 
	cd_plano_convenio_w 
from	Atend_categoria_convenio 
where	nr_seq_interno	= obter_atecaco_atendimento(nr_atendimento_p);
 
 
select	coalesce(max(1),0) 
into STRICT	qt_regra_w 
 
where 	exists ( SELECT 1 
		from	conv_regra_lib_atend 
		where	cd_convenio = cd_convenio_w);
		 
if (qt_regra_w = 1) then 
	 
	ie_permite_w:= 'N';
	nr_sequencia_w:= 0;
 
	open C02;
	loop 
	fetch C02 into 
		nr_interno_conta_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin 
		 
		select	max(obter_conta_paciente_etapa(nr_interno_conta_w,'C')) 
		into STRICT	nr_seq_etapa_w 
		;
		 
		open C01;
		loop 
		fetch C01 into	 
			ie_fecha_atendimento_w, 
			ie_fecha_conta_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
		end loop;
		close C01;
		 
		end;
	end loop;
	close C02;
 
	if (nr_sequencia_w > 0) then 
		ie_permite_w:= 'S';
	end if;
 
end if;
 
if (ie_opcao_p	= 1) then 
	ie_permite_w	:= ie_fecha_atendimento_w;
elsif (ie_opcao_p	= 2) then 
	ie_permite_w	:= ie_fecha_conta_w;
end if;
 
return	ie_permite_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
